import React, { useState } from 'react';
import './App.css';

function App() {
  const [jobPosting, setJobPosting] = useState(null);
  const [resume, setResume] = useState(null);
  const [questionsAndAnswers, setQuestionsAndAnswers] = useState([]);

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    if (name === 'jobPosting') {
      setJobPosting(files[0]);
    } else if (name === 'resume') {
      setResume(files[0]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('job_posting', jobPosting);
    formData.append('resume', resume);

    try {
      const response = await fetch('http://127.0.0.1:5000/upload', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      setQuestionsAndAnswers(data.questionsAndAnswers);
    } catch (error) {
      console.error('Error uploading files:', error);
    }
  };

  return (
    <div className="App">
      <h1>Interview Preparation Helper</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="jobPosting">Upload Job Posting PDF:</label>
          <input type="file" name="jobPosting" onChange={handleFileChange} />
        </div>
        <div>
          <label htmlFor="resume">Upload Resume PDF:</label>
          <input type="file" name="resume" onChange={handleFileChange} />
        </div>
        <button type="submit">Generate Questions and Answers</button>
      </form>
      <div className="questions-and-answers">
        {questionsAndAnswers.map((qa, index) => (
          <div key={index} className="qa-item">
            <div className="question">{qa.question}</div>
            <div className="answer">{qa.answer}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
