import React, { useState } from 'react';
import './App.css';

function App() {
  const [jobDescription, setJobDescription] = useState('');
  const [jobPosting, setJobPosting] = useState(null);
  const [resume, setResume] = useState(null);
  const [resumeText, setResumeText] = useState('');
  const [selectedQuestion, setSelectedQuestion] = useState('');
  const [questions, setQuestions] = useState([]);
  const [answer, setAnswer] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    if (name === 'jobPosting') {
      setJobPosting(files[0]);
    } else {
      setResume(files[0]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    const formData = new FormData();
    if (jobPosting) {
      formData.append('job_posting', jobPosting);
    }
    if (jobDescription) {
      formData.append('job_description', jobDescription);
    }
    formData.append('resume', resume);

    try {
      const response = await fetch('http://127.0.0.1:5000/upload', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      setQuestions(data.questions);
      setResumeText(data.resumeText);
    } catch (error) {
      console.error('Error uploading files:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleQuestionChange = (e) => setSelectedQuestion(e.target.value);

  const handleGenerateAnswer = async () => {
    if (!selectedQuestion || !resumeText) return;
    setIsLoading(true);

    try {
      const response = await fetch('http://127.0.0.1:5000/generate-answer', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ question: selectedQuestion, resume: resumeText }),
      });
      const data = await response.json();
      setAnswer(data.answer);
    } catch (error) {
      console.error('Error generating answer:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="App">
      <header>
        <h1>InterviewPrep.AI</h1>
        <p>Ace your next interview with AI-powered preparation!</p>
      </header>
      <main>
        <section className="upload-section">
          <h2>Upload Your Documents</h2>
          <form onSubmit={handleSubmit} className="upload-form">
            <div className="form-group">
              <label htmlFor="jobDescription">Job Description:</label>
              <textarea
                name="jobDescription"
                id="jobDescription"
                value={jobDescription}
                onChange={(e) => setJobDescription(e.target.value)}
                placeholder="Enter job description"
                rows={5}
              ></textarea>
            </div>
            <div className="form-group">
              <div className="file-upload">
                <input type="file" name="jobPosting" id="jobPosting" onChange={handleFileChange} accept=".pdf" />
                <label htmlFor="jobPosting" className="file-upload-label">
                  <i className="fas fa-cloud-upload-alt"></i> Choose File
                </label>
                {jobPosting && <span className="file-name">{jobPosting.name}</span>}
              </div>
            </div>
            <div className="form-group">
              <div className="file-upload">
                <input type="file" name="resume" id="resume" onChange={handleFileChange} accept=".pdf" />
                <label htmlFor="resume" className="file-upload-label">
                  <i className="fas fa-cloud-upload-alt"></i> Choose File
                </label>
                {resume && <span className="file-name">{resume.name}</span>}
              </div>
            </div>
            <button type="submit" className="btn-primary" disabled={isLoading}>
              {isLoading ? (
                <>
                  <i className="fas fa-spinner fa-spin"></i> Generating...
                </>
              ) : (
                'Generate Questions'
              )}
            </button>
          </form>
        </section>
        <section className="questions-section">
          <h2>Practice Questions</h2>
          {questions.length > 0 ? (
            <>
              <div className="question-select-container">
                <select value={selectedQuestion} onChange={handleQuestionChange} className="question-select">
                  <option value="">Select a question</option>
                  {questions.map((question, index) => (
                    <option key={index} value={question}>
                      {question}
                    </option>
                  ))}
                </select>
                <button onClick={handleGenerateAnswer} className="btn-secondary" disabled={isLoading}>
                  {isLoading ? (
                    <>
                      <i className="fas fa-spinner fa-spin"></i> Generating...
                    </>
                  ) : (
                    'Generate Answer'
                  )}
                </button>
              </div>
              {answer && (
                <div className="answer-section">
                  <h3>Sample Answer:</h3>
                  <p>{answer}</p>
                </div>
              )}
            </>
          ) : (
            <p>No questions generated yet. Upload your documents to get started.</p>
          )}
        </section>
      </main>
      <footer>
        <p>&copy; 2023 InterviewPrep.AI. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
