import React, { useState } from 'react';
import './App.css';

function App() {
  const [jobPosting, setJobPosting] = useState(null);
  const [resume, setResume] = useState(null);
  const [selectedQuestion, setSelectedQuestion] = useState('');
  const [questions, setQuestions] = useState([]);
  const [answer, setAnswer] = useState('');

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    if (name === 'jobPosting') {
      setJobPosting(files[0]);
    } else if (name === 'resume') {
      setResume(files[0]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('job_posting', jobPosting);
    formData.append('resume', resume);

    try {
      const response = await fetch('http://127.0.0.1:5000/upload', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      setQuestions(data.questions);
    } catch (error) {
      console.error('Error uploading files:', error);
    }
  };

  const handleQuestionChange = (e) => {
    setSelectedQuestion
    (e.target.value);
  };

  const handleGenerateAnswer = async () => {
    if (!selectedQuestion || !resume) return;

    try {
      const response = await fetch('http://127.0.0.1:5000/generate-answer', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          question: selectedQuestion,
          resume: resume
        }),
      });
      const data = await response.json();
      setAnswer(data.answer);
    } catch (error) {
      console.error('Error generating answer:', error);
    }
  };

  return (
    <div className="App">
      <h1>Interview Preparation Helper</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="jobPosting">Upload Job Posting PDF:</label>
          <input type="file" name="jobPosting" onChange={handleFileChange} />
        </div>
        <div>
          <label htmlFor="resume">Upload Resume PDF:</label>
          <input type="file" name="resume" onChange={handleFileChange} />
        </div>
        <button type="submit">Generate Questions</button>
      </form>
      {questions.length > 0 && (
        <div>
          <label htmlFor="questionSelect">Select a Question:</label>
          <select id="questionSelect" value={selectedQuestion} onChange={handleQuestionChange}>
            <option value="">--Please choose a question--</option>
            {questions.map((question, index) => (
              <option key={index} value={question}>
                {question}
              </option>
            ))}
          </select>
          <button onClick={handleGenerateAnswer}>Generate Answer</button>
        </div>
      )}
      {answer && (
        <div className="answer-section">
          <h3>Answer</h3>
          <p>{answer}</p>
        </div>
      )}
    </div>
  );
}

export default App;
