import React, { useState } from 'react';
import './App.css';

function App() {
  const [jobPosting, setJobPosting] = useState(null);
  const [resume, setResume] = useState(null);
  const [resumeText, setResumeText] = useState('');
  const [selectedQuestion, setSelectedQuestion] = useState('');
  const [questions, setQuestions] = useState([]);
  const [answer, setAnswer] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    if (name === 'jobPosting') {
      setJobPosting(files[0]);
    } else {
      setResume(files[0]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    const formData = new FormData();
    formData.append('job_posting', jobPosting);
    formData.append('resume', resume);

    try {
      const response = await fetch('http://127.0.0.1:5000/upload', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      setQuestions(data.questions);
      setResumeText(data.resumeText);
    } catch (error) {
      console.error('Error uploading files:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleQuestionChange = (e) => setSelectedQuestion(e.target.value);

  const handleGenerateAnswer = async () => {
    if (!selectedQuestion || !resumeText) return;
    setIsLoading(true);

    try {
      const response = await fetch('http://127.0.0.1:5000/generate-answer', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ question: selectedQuestion, resume: resumeText }),
      });
      const data = await response.json();
      setAnswer(data.answer);
    } catch (error) {
      console.error('Error generating answer:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="App">
      <header>
        <h1>Fastest 100 Question Interview Prep in the World!</h1>
      </header>
      <main>
        <div className="content-container">
          <div className="upload-section">
            <h2>Get Started by Uploading:</h2>
            <form onSubmit={handleSubmit} className="upload-form">
              <div className="file-input">
                <label htmlFor="jobPosting" className="custom-file-upload">
                  <i className="fas fa-file-pdf"></i> Upload Job Posting PDF
                </label>
                <input type="file" name="jobPosting" id="jobPosting" onChange={handleFileChange} accept=".pdf" />
                {jobPosting && <p>Uploaded: {jobPosting.name}</p>}
              </div>
              <div className="file-input">
                <label htmlFor="resume" className="custom-file-upload">
                  <i className="fas fa-file-pdf"></i> Upload Resume PDF
                </label>
                <input type="file" name="resume" id="resume" onChange={handleFileChange} accept=".pdf" />
                {resume && <p>Uploaded: {resume.name}</p>}
              </div>
              <button type="submit" className="submit-btn" disabled={isLoading}>
                {isLoading ? <div className="loading-icon"></div> : 'Generate Questions'}
              </button>
            </form>
          </div>
          <div className="questions-answer-container">
            {questions.length > 0 && (
              <div className="questions-section">
                <h2>Choose a Question to Practice</h2>
                <select value={selectedQuestion} onChange={handleQuestionChange} className="question-select">
                  <option value="">Select a Question</option>
                  {questions.map((question, index) => (
                    <option key={index} value={question}>
                      {question}
                    </option>
                  ))}
                </select>
                <button onClick={handleGenerateAnswer} className="generate-btn" disabled={isLoading}>
                  {isLoading ? <div className="loading-icon"></div> : 'Generate Answer'}
                </button>
              </div>
            )}
            {answer && (
              <div className="answer-section">
                <h2>Your Sample Answer</h2>
                <p>{answer}</p>
              </div>
            )}
          </div>
        </div>
      </main>
      <footer>
  <p>
    Powered by Groq: Model{' '}
    <a href="https://huggingface.co/mistralai/Mixtral-8x7B-Instruct-v0.1" target="_blank" rel="noopener noreferrer">
      Mixtral-8x7b-Instruct-v0.1
    </a>
  </p>
</footer>
    </div>
  );
}

export default App;
