import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [jobPostingFile, setJobPostingFile] = useState(null);
  const [resumeFile, setResumeFile] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState([]);

  const handleJobPostingUpload = (event) => {
    setJobPostingFile(event.target.files[0]);
  };

  const handleResumeUpload = (event) => {
    setResumeFile(event.target.files[0]);
  };

  const generateQuestions = async () => {
    const formData = new FormData();
    formData.append('jobPosting', jobPostingFile);

    try {
      const response = await axios.post('http://127.0.0.1:5000/api/generate-questions', formData);
      setQuestions(response.data.questions);
    } catch (error) {
      console.error('Error generating questions:', error);
    }
  };

  const generateAnswers = async () => {
    const formData = new FormData();
    formData.append('resume', resumeFile);
    formData.append('questions', JSON.stringify(questions));

    try {
      const response = await axios.post('http://127.0.0.1:5000/api/generate-answers', formData);
      setAnswers(response.data.answers);
    } catch (error) {
      console.error('Error generating answers:', error);
    }
  };

  return (
    <div className="app">
      <h1>Interview Question and Answer Generator</h1>
      <div>
        <h2>Upload Job Posting</h2>
        <input type="file" accept=".pdf" onChange={handleJobPostingUpload} />
        <button onClick={generateQuestions}>Generate Questions</button>
      </div>
      <div>
        <h2>Upload Resume</h2>
        <input type="file" accept=".pdf" onChange={handleResumeUpload} />
        <button onClick={generateAnswers}>Generate Answers</button>
      </div>
      <div>
        <h2>Generated Questions</h2>
        <ul>
          {questions.map((question, index) => (
            <li key={index}>{question}</li>
          ))}
        </ul>
      </div>
      <div>
        <h2>Generated Answers</h2>
        <ul>
          {answers.map((answer, index) => (
            <li key={index}>{answer}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
