import React, { useState } from 'react';
import axios from 'axios';

const JobInterviewHelper = () => {
  const [jobPostingFile, setJobPostingFile] = useState(null);
  const [resumeFile, setResumeFile] = useState(null);
  const [interviewQuestions, setInterviewQuestions] = useState([]);
  const [generatingAnswers, setGeneratingAnswers] = useState(false);
  const [answers, setAnswers] = useState([]);

  const handleJobPostingUpload = (event) => {
    setJobPostingFile(event.target.files[0]);
  };

  const handleResumeUpload = (event) => {
    setResumeFile(event.target.files[0]);
  };

  const handleGenerateQuestions = async () => {
    if (jobPostingFile) {
      const formData = new FormData();
      formData.append('jobPosting', jobPostingFile);

      try {
        const response = await axios.post('/api/generate-questions', formData);
        setInterviewQuestions(response.data.questions);
      } catch (error) {
        console.error('Error generating interview questions:', error);
      }
    }
  };

  const handleGenerateAnswers = async () => {
    if (resumeFile) {
      setGeneratingAnswers(true);
      const formData = new FormData();
      formData.append('resume', resumeFile);
      formData.append('questions', JSON.stringify(interviewQuestions));

      try {
        const response = await axios.post('/api/generate-answers', formData);
        setAnswers(response.data.answers);
      } catch (error) {
        console.error('Error generating answers:', error);
      }
      setGeneratingAnswers(false);
    }
  };

  return (
    <div>
      <h1>Job Interview Helper</h1>
      <div>
        <h2>Upload Job Posting</h2>
        <input type="file" accept=".pdf" onChange={handleJobPostingUpload} />
        <button onClick={handleGenerateQuestions} disabled={!jobPostingFile}>
          Generate Interview Questions
        </button>
      </div>
      <div>
        <h2>Upload Resume</h2>
        <input type="file" accept=".pdf" onChange={handleResumeUpload} />
        <button onClick={handleGenerateAnswers} disabled={!resumeFile || generatingAnswers}>
          Generate Answers
        </button>
      </div>
      <div>
        <h2>Generated Interview Questions</h2>
        <ul>
          {interviewQuestions.map((question, index) => (
            <li key={index}>{question}</li>
          ))}
        </ul>
      </div>
      <div>
        <h2>Generated Answers</h2>
        {generatingAnswers ? (
          <p>Generating answers...</p>
        ) : (
          <ul>
            {answers.map((answer, index) => (
              <li key={index}>
                <p><strong>Question:</strong> {interviewQuestions[index]}</p>
                <p><strong>Answer:</strong> {answer}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default JobInterviewHelper;
